        Console.log("code = {}", code);

        // 2、执行登陆
        Console.log("2、执行登陆, 获取token");
        String[] loginRes = login(code);
        Console.log("user_id = {}", loginRes[0]);
        Console.log("login_token = {}", loginRes[1]);
        Console.log("app_token = {}", loginRes[2]);

        // 3、获取app_token
