    private String getCode(MiUser miUser) {
        String account =miUser.getAccount();
        if (!Validator.isEmail(account)) {
            account = "+86" + account;
        }
        HttpRequest post = HttpUtil.createPost( URLUtil.encode( UrlConstant.LOGIN_CODE_URL.replace("{USERNAME}", account)));
        post.contentType("application/x-www-form-urlencoded;charset=UTF-8");
        post.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00");


        post.form("client_id", "HuaMi");
        post.form("password", miUser.getPassword());
        post.form("redirect_uri", UrlConstant.REDIRECT_URL);
        post.form("token", "access");
        HttpResponse execute = post.execute();
        // 得到重定向的url 获取里面的access
        // https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html?region=cn-northwest-1&access=NvtykCjHbhnFYOL8E8_SS&country_code=CN&expiration=1605504330
        String location = execute.header(Header.LOCATION);
        Console.log(location);
        List<String> split = StrUtil.split(location, "&");
        Optional<String> first = split.stream().filter(param -> param.startsWith("access=")).findFirst();
        return first.map(s -> s.replace("access=", "")).orElse(null);
    }

    private String[] login(String code) {
        HttpRequest post = HttpUtil.createPost(UrlConstant.LOGIN_URL);
        post.contentType("application/x-www-form-urlencoded;charset=UTF-8");
        post.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00");


        post.form("app_name", "com.xiaomi.hm.health");
        post.form("app_version", "4.6.0");
        post.form("code", code);
        post.form("country_code", "CN");
        post.form("device_id", "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1");
        post.form("device_model", "phone");
        post.form("grant_type", "access_token");
        post.form("third_name", "huami_phone");

        HttpResponse execute = post.execute();
        String body = execute.body();
        JSONObject resp = JSONUtil.parseObj(body);


        Console.log("loginR: {}", resp);
        String tokenInfo = resp.getStr("token_info");

        if (StrUtil.isEmpty(tokenInfo)) {
            throw new RuntimeException("获取login_token失败, 请检查账户名或密码是否正确");
        }

        // = 替换为 : 便于格式化
        String replace = tokenInfo.replace("=", ":");
        JSONObject tokenJson = JSONUtil.parseObj(replace);
        Console.log("login_token: {}", tokenJson.getStr("login_token"));

        return new String[]{tokenJson.getStr("user_id"), tokenJson.getStr("login_token"), tokenJson.getStr("app_token")};
    }

    private Long getTime() {
        try {
            String resp = HttpUtil.get(UrlConstant.GET_TIME_URL);
            JSONObject res = JSONUtil.parseObj(resp);
            return res.getJSONObject("data").getLong("t") / 1000;
        } catch (Exception e) {
            return System.currentTimeMillis() / 1000;
        }
    }

